mapper.select:
SELECT * FROM customer


         customer_gender = #{customer_gender}


         customer_jurisdiction = #{customer_jurisdiction}


         customer_name LIKE CONCAT('%',#{customer_name},'%')


         customer_userid = #{customer_userid}


         customer_id = #{customer_id}

mapper.update:
UPDATE customer


        customer_number = #{customer_number}


        customer_address = #{customer_address}


        customer_text = #{customer_text}


WHERE customer_id = #{customer_id}

mapper.select:
select count(customer_id) from customer


        customer_gender = #{gender}


        customer_userid = #{id}

select customer_id ,customer_name,customer_number,customer_text,customer_userid,user_name,clue_date,clue_state from customer c
left join `user` u
on customer_userid=user_id
left join clue cl
on customer_id=clue_custome


        and customer_id=#{customer_id}


       and u.user_id=#{user_id}


    AND cl.clue_date LIKE CONCAT ('%',#{clue_date},'%')


    AND u.user_jurisdiction  <= #{user_jurisdiction}


    AND u.user_name LIKE CONCAT ('%',#{user_name},'%')


group by customer_name,clue_date
order by clue_date desc

    limit #{from},#{pageSize};

select count(1) from( select customer_id ,customer_name,customer_number,customer_text,customer_userid,user_name,clue_date,clue_state from customer c left join `user` u on customer_userid=user_id left join clue cl on customer_id=clue_custome
and u.user_id=#{user_id}
AND cl.clue_date LIKE CONCAT ('%',#{clue_date},'%')
AND u.user_jurisdiction <= #{user_jurisdiction}
AND u.user_name LIKE CONCAT ('%',#{user_name},'%')
group by customer_name,clue_date order by clue_date desc ) A
